import React, {useState} from 'react'

export default function List() {

    const [data, setData] = useState([]);//los corchetes son para especificar que la variable "data" va a recibir un objeto
    //constante con hook para capturar los datos de la petición fetch
    fetch('https://api-example-udb.herokuapp.com/api/list/4')
    .then((res)=>res.json()).then((resJson)=>setData(resJson));




  return (
    <div style={{background:'lightblue'}}>
        <p style={{fontSize:30, textAlign:'center'}}>List</p>
        <p style={{fontSize:30, textAlign:'center'}}>{data.name}</p>
        <p style={{fontSize:30, textAlign:'center'}}>{data.team}</p>
        <p style={{fontSize:30, textAlign:'center'}}>{data.country}</p>
    </div>
  )
}
